import React, { Component } from 'react'
import '../Preview/Preview.css'
import axios from 'axios'
import { Link } from 'react-router-dom'
import { useHistory } from "react-router-dom";

export default class Preview extends Component {

    constructor(props) {
        super(props)
        const { history } = this.props;
        console.log(history)
        this.state = {
            IndividualPost: [],
            PostId: this.props.match.params.id,
            UserDetails: []
        }
    }


    async componentDidMount() {
        let postDetails = await axios.get(`http://jsonplaceholder.typicode.com/posts/${this.state.PostId}`);
        this.setState({
            IndividualPost: postDetails.data
        })
        let user = await axios.get(`https://jsonplaceholder.typicode.com/users/${this.state.IndividualPost.userId}`);
        this.setState({
            UserDetails: user.data
        })
        console.log(this.state.UserDetails)
    }

    render() {
        const id2 = this.props.match.params.id
        return (
            <div>

                <div class="card">
                    {/* <img src={this.props.productItem.imgUrl}></img> */}
                    <h1>Title</h1>
                    <h1>{this.state.IndividualPost.title}</h1>
                    <p class="price">Created By : {this.state.UserDetails.name}</p>

                    <p><button>Add to Cart</button></p>
                    
                </div>
                <p><button><Link onClick={() => this.history.push("/PostList")}>Back</Link></button></p>

            </div>
        )
    }
}
